"use strict";

window.addEventListener('DOMContentLoaded', function () {
  var el = document.querySelector('.welcome__sem');

  // Після завершення першої анімації
  el.addEventListener('animationend', function () {
    el.classList.add('levitation');
  });
});

// slider
var cards = document.querySelectorAll('.card');
var dots = document.querySelectorAll('.dot');
var current = 0;
var isAnimating = false; // ✅ прапорець для блокування під час анімації

function showSlide(index) {
  if (index === current || isAnimating) return; // ⛔ блокуємо повтор
  isAnimating = true; // 🚫 ставимо блок

  var prevCard = cards[current];
  prevCard.classList.remove('active');
  prevCard.classList.add('exit');
  setTimeout(function () {
    prevCard.classList.remove('exit');
    isAnimating = false; // ✅ знімаємо блок після завершення
  }, 600); // ⏱️ має збігатися з transition у CSS

  current = (index + cards.length) % cards.length;
  cards[current].classList.add('active');
  dots.forEach(function (dot) {
    return dot.classList.remove('active');
  });
  dots[current].classList.add('active');
}
document.querySelector('.arrow.next').addEventListener('click', function () {
  showSlide(current + 1);
});
document.querySelector('.arrow.prev').addEventListener('click', function () {
  showSlide(current - 1);
});
var startX = 0;
var endX = 0;
document.querySelector('.slider').addEventListener('touchstart', function (e) {
  startX = e.touches[0].clientX;
});
document.querySelector('.slider').addEventListener('touchend', function (e) {
  endX = e.changedTouches[0].clientX;
  var diff = endX - startX;
  if (Math.abs(diff) > 50) {
    if (diff < 0) {
      showSlide(current + 1); // swipe left
    } else {
      showSlide(current - 1); // swipe right
    }
  }
});

dots.forEach(function (dot, index) {
  dot.addEventListener('click', function () {
    showSlide(index);
  });
});
